mapper.select:
SELECT DISTINCT USER.ID, USER.LOGIN_ID, USER.NAME, USER.EMAIL, CASE USER.STATUS WHEN 'A' THEN '正常' WHEN 'L' THEN '已锁定' WHEN 'I' THEN '未激活' WHEN 'E' THEN '已过期' WHEN 'V' THEN '已注销' END AS STATUS, DATE_FORMAT(USER.LAST_LOGIN_SUCCESS_DT, '%Y/%m/%d %T') AS LAST_LOGIN_SUCCESS_DT, DATE_FORMAT(USER.ACTIVATED_AT, '%Y/%m/%d') AS ACTIVATED_AT, DATE_FORMAT(USER.DEACTIVATED_AT, '%Y/%m/%d') AS DEACTIVATED_AT, ROLE.ROLE_NAME AS ROLE, ROLE.ID AS ROLE_ID, PJT.NAME AS PROJECT FROM AC_USER_ACCT USER LEFT JOIN AC_USER_ROLE_ASSIGN ASN ON USER.ID = ASN.USER_ACCT_ID LEFT JOIN AC_ROLE ROLE ON ASN.ROLE_ID = ROLE.ID LEFT JOIN PJT_USER_PROJECT_ASN PSN ON PSN.USER_ACCT_ID = USER.ID LEFT JOIN PJT_PROJECT PJT ON PJT.ID = PSN.PROJECT_ID LEFT JOIN PJT_PROJECT_GROUP GRP ON PJT.GROUP_ID = GRP.ID
AND UPPER(LOGIN_ID) LIKE CONCAT ('%', UPPER(#{sp.params.loginId}), '%')
AND UPPER(EMAIL) LIKE CONCAT ('%', UPPER(#{sp.params.email}), '%')
AND ROLE.ID = #{sp.params.roleParam}
AND PJT.ID = #{sp.params.project}
AND PSN.GROUP_ID IN (SELECT GROUP_ID FROM PJT_USER_PROJECT_ASN WHERE USER_ACCT_ID = #{sp.params.userId})
AND ASN.ROLE_ID = 'DEV'
AND ASN.ROLE_ID = 'QA'
GROUP BY USER.ID
ORDER BY LOGIN_ID ${sp.order}
ORDER BY EMAIL ${sp.order}
ORDER BY ROLE ${sp.order}
ORDER BY PROJECT ${sp.order}
ORDER BY STATUS ${sp.order}
ORDER BY LAST_LOGIN_SUCCESS_DT ${sp.order}
LIMIT ${(sp.pageNo - 1) * sp.pageSize}, #{sp.pageSize};

SELECT PJT.NAME AS PROJECT_NAME, PJT.ID AS PROJECT_ID, PJT.IS_PUBLIC AS IS_PUBLIC, GRP.ID AS GROUP_ID, GRP.NAME AS GROUP_NAME, DATE_FORMAT(PSN.CREATED_AT, '%Y/%m/%d') AS ASSIGNED_AT, PSN.CREATED_BY AS ASSIGNED_BY
FROM PJT_PROJECT PJT LEFT JOIN PJT_USER_PROJECT_ASN PSN ON PJT.ID = PSN.PROJECT_ID LEFT JOIN PJT_PROJECT_GROUP GRP ON PJT.GROUP_ID = GRP.ID WHERE PSN.USER_ACCT_ID = #{param1}
UNION ALL SELECT PJT.NAME AS PROJECT_NAME, PJT.ID AS PROJECT_ID, PJT.IS_PUBLIC AS IS_PUBLIC, GRP.ID AS GROUP_ID, GRP.NAME AS GROUP_NAME, DATE_FORMAT(PSN.CREATED_AT, '%Y/%m/%d') AS ASSIGNED_AT, PSN.CREATED_BY AS ASSIGNED_BY
FROM PJT_PROJECT PJT LEFT JOIN PJT_USER_PROJECT_ASN PSN ON PJT.GROUP_ID = PSN.GROUP_ID LEFT JOIN PJT_PROJECT_GROUP GRP ON PSN.GROUP_ID = GRP.ID WHERE PSN.USER_ACCT_ID = #{param1}
